import React, {useEffect, useState} from 'react'
import DeviceList from './components/DeviceList'
import { getDevices } from './services/api'
import { connectWebSocket } from './services/ws'
import './styles.css'

export default function App(){
  const [devices, setDevices] = useState([])
  const [loading, setLoading] = useState(true)

  const load = async ()=>{
    try{
      setLoading(true)
      const d = await getDevices()
      setDevices(d)
    }catch(e){
      setDevices([])
    }finally{
      setLoading(false)
    }
  }

  useEffect(()=>{
    load()
    const t = setInterval(load, 5000)
    return ()=>clearInterval(t)
  },[])

  // WebSocket for real-time updates from backend (MQTT -> backend -> WS)
  useEffect(()=>{
    const conn = connectWebSocket((msg)=>{
      try{
        if(!msg) return
        if(msg.type === 'mqtt' && msg.topic){
          // topic format: home/{device_type}/{device_id}/...
          const parts = msg.topic.split('/')
          const deviceId = parts[2]
          if(!deviceId) return
          setDevices(prev => {
            const idx = prev.findIndex(d => d.id === deviceId || d.device_id === deviceId)
            if(idx === -1) return prev
            const copy = [...prev]
            // merge payload into device state where appropriate
            const payload = msg.payload || {}
            copy[idx] = {
              ...copy[idx],
              last_telemetry: payload,
              state: payload.state || copy[idx].state
            }
            return copy
          })
        }
      }catch(e){
        console.warn('ws msg handler error', e)
      }
    })
    return ()=>conn.close()
  }, [])

  return (
    <div className='app-root'>
      <div className='header'>
        <div className='title'>
          <h1>üè° IoT Home Dashboard</h1>
          <div className='small'>MQTT & Web UI</div>
        </div>
        <div>
          <button className='btn' onClick={load}>Refresh</button>
        </div>
      </div>

      <DeviceList devices={devices} loading={loading} />
    </div>
  )
}
